{
  "name": "Gestor de Desvios de Motoristas",
  "description": "Uma ferramenta completa para upload, edição e visualização de desvios de motoristas com dashboards interativos e exportação de dados tratados para Excel."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface DeviationRecord {
  id: string;
  MOTORISTAS: string;
  "TIPO DE DESVIO": string;
  QTD: number;
  "MÊS": string;
  TRATADO: string;
  TRATATIVA: string;
  DATA: string; // ISO String or YYYY-MM-DD
  STATUS: string;
  "APLICADO POR": string;
  // Treated columns
  ANO?: number;
  MES_NUM?: number;
  SEMANA?: number;
  isValid?: boolean;
}

export interface ValidationError {
  row: number;
  field: string;
  message: string;
}

export enum MonthName {
  JANEIRO = "JANEIRO",
  FEVEREIRO = "FEVEREIRO",
  MARCO = "MARCO",
  ABRIL = "ABRIL",
  MAIO = "MAIO",
  JUNHO = "JUNHO",
  JULHO = "JULHO",
  AGOSTO = "AGOSTO",
  SETEMBRO = "SETEMBRO",
  OUTUBRO = "OUTUBRO",
  NOVEMBRO = "NOVEMBRO",
  DEZEMBRO = "DEZEMBRO"
}

export const MONTH_MAP: Record<string, number> = {
  "JANEIRO": 1, "FEVEREIRO": 2, "MARCO": 3, "ABRIL": 4, "MAIO": 5, "JUNHO": 6,
  "JULHO": 7, "AGOSTO": 8, "SETEMBRO": 9, "OUTUBRO": 10, "NOVEMBRO": 11, "DEZEMBRO": 12
};

import * as XLSX from 'xlsx';
import { DeviationRecord, MonthName, MONTH_MAP, ValidationError } from '../types';

export const cleanData = (data: any[]): { cleaned: DeviationRecord[], errors: ValidationError[] } => {
  const cleaned: DeviationRecord[] = [];
  const errors: ValidationError[] = [];

  data.forEach((row, index) => {
    const rowIdx = index + 1;

    // Normalize column mapping if the input has slight variations
    const motorista = String(row['MOTORISTAS'] || '').trim();
    const tipoDesvio = String(row['TIPO DE DESVIO'] || '').trim();
    const qtd = parseInt(row['QTD']) || 0;
    let dataRaw = row['DATA'];

    // Basic Validation
    if (!motorista) errors.push({ row: rowIdx, field: 'MOTORISTAS', message: 'Campo obrigatório' });
    if (!tipoDesvio) errors.push({ row: rowIdx, field: 'TIPO DE DESVIO', message: 'Campo obrigatório' });
    if (!dataRaw) errors.push({ row: rowIdx, field: 'DATA', message: 'Campo obrigatório' });

    // Date Processing
    let dateObj: Date | null = null;
    if (typeof dataRaw === 'number') {
      // Excel date format
      dateObj = XLSX.SSF.parse_date_code(dataRaw) ? new Date((dataRaw - 25569) * 86400 * 1000) : null;
    } else if (dataRaw) {
      dateObj = new Date(dataRaw);
    }

    const isValid = motorista && tipoDesvio && dateObj && !isNaN(dateObj.getTime());

    if (dateObj && !isNaN(dateObj.getTime())) {
      const ano = dateObj.getFullYear();
      const mesNum = dateObj.getMonth() + 1;

      // Calculate ISO week
      const d = new Date(dateObj);
      d.setHours(0, 0, 0, 0);
      d.setDate(d.getDate() + 4 - (d.getDay() || 7));
      const yearStart = new Date(d.getFullYear(), 0, 1);
      const semana = Math.ceil((((d.getTime() - yearStart.getTime()) / 86400000) + 1) / 7);

      const mesNome = Object.keys(MONTH_MAP)[mesNum - 1];

      cleaned.push({
        id: crypto.randomUUID(),
        MOTORISTAS: motorista,
        "TIPO DE DESVIO": tipoDesvio,
        QTD: isNaN(qtd) ? 0 : qtd,
        "MÊS": String(row['MÊS'] || mesNome).toUpperCase().trim(),
        TRATADO: String(row['TRATADO'] || 'NÃO').toUpperCase().trim(),
        TRATATIVA: String(row['TRATATIVA'] || '').trim(),
        DATA: dateObj.toISOString().split('T')[0],
        STATUS: String(row['STATUS'] || '').trim(),
        "APLICADO POR": String(row['APLICADO POR'] || '').trim(),
        ANO: ano,
        MES_NUM: mesNum,
        SEMANA: semana,
        isValid: !!isValid
      });
    } else {
      // Keep invalid rows for editing but mark them
       cleaned.push({
        id: crypto.randomUUID(),
        MOTORISTAS: motorista,
        "TIPO DE DESVIO": tipoDesvio,
        QTD: isNaN(qtd) ? 0 : qtd,
        "MÊS": String(row['MÊS'] || '').toUpperCase(),
        TRATADO: String(row['TRATADO'] || 'NÃO').toUpperCase(),
        TRATATIVA: String(row['TRATATIVA'] || ''),
        DATA: String(dataRaw || ''),
        STATUS: String(row['STATUS'] || ''),
        "APLICADO POR": String(row['APLICADO POR'] || ''),
        isValid: false
      });
    }
  });

  return { cleaned, errors };
};

export const exportToExcel = (originalData: DeviationRecord[], treatedData: DeviationRecord[]) => {
  const wb = XLSX.utils.book_new();

  // Create sheet 1: Original/Current state
  const ws1 = XLSX.utils.json_to_sheet(originalData.map(({id, isValid, ...rest}) => rest));
  XLSX.utils.book_append_sheet(wb, ws1, "BASE_ATUAL");

  // Create sheet 2: BASE_TRATADA (Cleaned and formatted)
  const ws2 = XLSX.utils.json_to_sheet(treatedData.filter(r => r.isValid).map(({id, isValid, ...rest}) => rest));
  XLSX.utils.book_append_sheet(wb, ws2, "BASE_TRATADA");

  XLSX.writeFile(wb, "gestao_desvios_atualizada.xlsx");
};

import React, { useMemo, useState } from 'react';
import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, 
  LineChart, Line, PieChart, Pie, Cell, ScatterChart, Scatter, ZAxis
} from 'recharts';
import { DeviationRecord, MONTH_MAP } from '../types';
import { Filter, PieChart as PieIcon, BarChart3, TrendingUp, Users, AlertCircle } from 'lucide-react';

interface DashboardProps {
  data: DeviationRecord[];
}

const COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899', '#06b6d4'];

const Dashboard: React.FC<DashboardProps> = ({ data }) => {
  const [filters, setFilters] = useState({
    motorista: 'TODOS',
    status: 'TODOS',
    mes: 'TODOS',
    tratativa: 'TODOS'
  });

  const filteredData = useMemo(() => {
    return data.filter(r => {
      const matchMotorista = filters.motorista === 'TODOS' || r.MOTORISTAS === filters.motorista;
      const matchStatus = filters.status === 'TODOS' || r.STATUS === filters.status;
      const matchMes = filters.mes === 'TODOS' || r["MÊS"] === filters.mes;
      const matchTratativa = filters.tratativa === 'TODOS' || r.TRATATIVA === filters.tratativa;
      return matchMotorista && matchStatus && matchMes && matchTratativa && r.isValid;
    });
  }, [data, filters]);

  // Options for filters
  const options = useMemo(() => {
    const validData = data.filter(r => r.isValid);
    return {
      motoristas: ['TODOS', ...Array.from(new Set(validData.map(r => r.MOTORISTAS)))].sort(),
      status: ['TODOS', ...Array.from(new Set(validData.map(r => r.STATUS)))].sort(),
      meses: ['TODOS', ...Array.from(new Set(validData.map(r => r["MÊS"])))].sort((a, b) => (MONTH_MAP[a] || 0) - (MONTH_MAP[b] || 0)),
      tratativas: ['TODOS', ...Array.from(new Set(validData.map(r => r.TRATATIVA)))].sort()
    };
  }, [data]);

  // KPIs
  const kpis = useMemo(() => {
    const totalRegistros = filteredData.length;
    const totalDesvios = filteredData.reduce((acc, curr) => acc + (Number(curr.QTD) || 0), 0);
    const totalTratados = filteredData.filter(r => r.TRATADO === 'SIM').length;
    const percTratado = totalRegistros > 0 ? (totalTratados / totalRegistros * 100).toFixed(1) : 0;

    const motoristaCounts: Record<string, number> = {};
    const desvioCounts: Record<string, number> = {};

    filteredData.forEach(r => {
      motoristaCounts[r.MOTORISTAS] = (motoristaCounts[r.MOTORISTAS] || 0) + Number(r.QTD);
      desvioCounts[r["TIPO DE DESVIO"]] = (desvioCounts[r["TIPO DE DESVIO"]] || 0) + Number(r.QTD);
    });

    const topMotorista = Object.entries(motoristaCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || '-';
    const topDesvio = Object.entries(desvioCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || '-';

    return { totalRegistros, totalDesvios, percTratado, topMotorista, topDesvio };
  }, [filteredData]);

  // Chart Data
  const topMotoristasData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r.MOTORISTAS] = (counts[r.MOTORISTAS] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 10);
  }, [filteredData]);

  const topDesviosData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r["TIPO DE DESVIO"]] = (counts[r["TIPO DE DESVIO"]] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 10);
  }, [filteredData]);

  const timelineData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      const date = r.DATA;
      counts[date] = (counts[date] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([date, value]) => ({ date, value }))
      .sort((a, b) => a.date.localeCompare(b.date));
  }, [filteredData]);

  const statusData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r.STATUS] = (counts[r.STATUS] || 0) + 1;
    });
    return Object.entries(counts).map(([name, value]) => ({ name, value }));
  }, [filteredData]);

  return (
    <div className="space-y-6 pb-20">
      {/* Filters */}
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Motorista</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.motorista}
            onChange={e => setFilters(prev => ({...prev, motorista: e.target.value}))}
          >
            {options.motoristas.map(m => <option key={m} value={m}>{m}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Mês</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.mes}
            onChange={e => setFilters(prev => ({...prev, mes: e.target.value}))}
          >
            {options.meses.map(m => <option key={m} value={m}>{m}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Status</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.status}
            onChange={e => setFilters(prev => ({...prev, status: e.target.value}))}
          >
            {options.status.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Tratativa</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.tratativa}
            onChange={e => setFilters(prev => ({...prev, tratativa: e.target.value}))}
          >
            {options.tratativas.map(t => <option key={t} value={t}>{t}</option>)}
          </select>
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
        {[
          { label: 'Registros', value: kpis.totalRegistros, icon: <AlertCircle className="w-5 h-5 text-blue-600" />, color: 'bg-blue-50' },
          { label: 'Total Desvios', value: kpis.totalDesvios, icon: <BarChart3 className="w-5 h-5 text-purple-600" />, color: 'bg-purple-50' },
          { label: '% Tratado', value: `${kpis.percTratado}%`, icon: <TrendingUp className="w-5 h-5 text-green-600" />, color: 'bg-green-50' },
          { label: 'Top Motorista', value: kpis.topMotorista, icon: <Users className="w-5 h-5 text-orange-600" />, color: 'bg-orange-50', small: true },
          { label: 'Top Desvio', value: kpis.topDesvio, icon: <PieIcon className="w-5 h-5 text-red-600" />, color: 'bg-red-50', small: true },
        ].map((kpi, i) => (
          <div key={i} className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 flex flex-col justify-between">
            <div className="flex justify-between items-start mb-2">
              <span className="text-xs font-bold text-gray-400 uppercase">{kpi.label}</span>
              <div className={`p-1.5 rounded-lg ${kpi.color}`}>{kpi.icon}</div>
            </div>
            <div className={`font-bold text-gray-800 ${kpi.small ? 'text-sm' : 'text-2xl'}`}>{kpi.value}</div>
          </div>
        ))}
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Bar: Top 10 Drivers */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <Users className="w-4 h-4" /> Top 10 Motoristas (QTD)
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <BarChart data={topMotoristasData} layout="vertical" margin={{ left: 20 }}>
              <CartesianGrid strokeDasharray="3 3" horizontal={false} stroke="#f0f0f0" />
              <XAxis type="number" hide />
              <YAxis dataKey="name" type="category" width={100} fontSize={10} tick={{ fill: '#6b7280' }} />
              <Tooltip cursor={{ fill: '#f9fafb' }} contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Bar dataKey="value" fill="#3b82f6" radius={[0, 4, 4, 0]} barSize={20} />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Line: Trend */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <TrendingUp className="w-4 h-4" /> Tendência de Desvios (QTD por Data)
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <LineChart data={timelineData}>
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f0f0f0" />
              <XAxis dataKey="date" fontSize={10} tick={{ fill: '#6b7280' }} />
              <YAxis fontSize={10} tick={{ fill: '#6b7280' }} />
              <Tooltip contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Line type="monotone" dataKey="value" stroke="#8b5cf6" strokeWidth={3} dot={{ r: 4, fill: '#8b5cf6' }} activeDot={{ r: 6 }} />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Bar: Top 10 Deviations */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <PieIcon className="w-4 h-4" /> Tipos de Desvio (QTD)
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <BarChart data={topDesviosData}>
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f0f0f0" />
              <XAxis dataKey="name" hide />
              <YAxis fontSize={10} tick={{ fill: '#6b7280' }} />
              <Tooltip cursor={{ fill: '#f9fafb' }} contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Bar dataKey="value" fill="#f59e0b" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Pie: Status Distribution */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <Filter className="w-4 h-4" /> Distribuição por Status
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <PieChart>
              <Pie
                data={statusData}
                innerRadius={60}
                outerRadius={100}
                paddingAngle={5}
                dataKey="value"
              >
                {statusData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Legend verticalAlign="bottom" height={36} iconType="circle" />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React from 'react';
import { DeviationRecord } from '../types';
import { Trash2, Plus, AlertCircle, CheckCircle2 } from 'lucide-react';

interface DataEditorProps {
  data: DeviationRecord[];
  onUpdate: (newData: DeviationRecord[]) => void;
}

const DataEditor: React.FC<DataEditorProps> = ({ data, onUpdate }) => {
  const handleCellChange = (id: string, field: keyof DeviationRecord, value: any) => {
    const newData = data.map(item => {
      if (item.id === id) {
        return { ...item, [field]: value };
      }
      return item;
    });
    onUpdate(newData);
  };

  const addRow = () => {
    const newRow: DeviationRecord = {
      id: crypto.randomUUID(),
      MOTORISTAS: '',
      'TIPO DE DESVIO': '',
      QTD: 1,
      'MÊS': '',
      TRATADO: 'NÃO',
      TRATATIVA: '',
      DATA: new Date().toISOString().split('T')[0],
      STATUS: '',
      'APLICADO POR': '',
      isValid: false
    };
    onUpdate([newRow, ...data]);
  };

  const removeRow = (id: string) => {
    onUpdate(data.filter(item => item.id !== id));
  };

  const columns: { label: string; key: keyof DeviationRecord; type: string }[] = [
    { label: 'Status Data', key: 'isValid', type: 'indicator' },
    { label: 'Motorista', key: 'MOTORISTAS', type: 'text' },
    { label: 'Tipo Desvio', key: 'TIPO DE DESVIO', type: 'text' },
    { label: 'QTD', key: 'QTD', type: 'number' },
    { label: 'Mês', key: 'MÊS', type: 'text' },
    { label: 'Data', key: 'DATA', type: 'date' },
    { label: 'Tratado', key: 'TRATADO', type: 'text' },
    { label: 'Tratativa', key: 'TRATATIVA', type: 'text' },
    { label: 'Status', key: 'STATUS', type: 'text' },
    { label: 'Aplicador', key: 'APLICADO POR', type: 'text' },
  ];

  return (
    <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden flex flex-col h-full">
      <div className="p-4 border-b border-gray-100 flex justify-between items-center bg-gray-50/50">
        <h2 className="text-sm font-bold text-gray-700">Edição de Registros ({data.length})</h2>
        <button 
          onClick={addRow}
          className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-3 py-1.5 rounded-lg text-xs font-semibold transition-colors shadow-sm"
        >
          <Plus className="w-4 h-4" /> Adicionar Linha
        </button>
      </div>

      <div className="overflow-x-auto flex-1">
        <table className="w-full text-left border-collapse table-fixed min-w-[1200px]">
          <thead className="bg-gray-50 text-gray-500 uppercase text-[10px] font-bold tracking-wider sticky top-0 z-10">
            <tr>
              {columns.map(col => (
                <th key={col.key} className={`p-3 border-b border-gray-100 ${col.key === 'isValid' ? 'w-16' : ''}`}>
                  {col.label}
                </th>
              ))}
              <th className="p-3 border-b border-gray-100 w-16">Ações</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-50 text-sm">
            {data.map((row) => (
              <tr key={row.id} className="hover:bg-blue-50/30 transition-colors">
                <td className="p-3 text-center">
                  {row.isValid ? (
                    <CheckCircle2 className="w-5 h-5 text-green-500 mx-auto" title="Válido" />
                  ) : (
                    <AlertCircle className="w-5 h-5 text-red-500 mx-auto" title="Inválido/Incompleto" />
                  )}
                </td>
                <td className="p-1">
                  <input 
                    type="text" 
                    value={row.MOTORISTAS} 
                    onChange={e => handleCellChange(row.id, 'MOTORISTAS', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none transition-all"
                  />
                </td>
                <td className="p-1">
                  <input 
                    type="text" 
                    value={row["TIPO DE DESVIO"]} 
                    onChange={e => handleCellChange(row.id, 'TIPO DE DESVIO', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-1">
                  <input 
                    type="number" 
                    value={row.QTD} 
                    onChange={e => handleCellChange(row.id, 'QTD', parseInt(e.target.value))}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-1">
                  <input 
                    type="text" 
                    value={row["MÊS"]} 
                    onChange={e => handleCellChange(row.id, 'MÊS', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-1">
                  <input 
                    type="date" 
                    value={row.DATA} 
                    onChange={e => handleCellChange(row.id, 'DATA', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-1">
                  <select 
                    value={row.TRATADO} 
                    onChange={e => handleCellChange(row.id, 'TRATADO', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  >
                    <option value="SIM">SIM</option>
                    <option value="NÃO">NÃO</option>
                  </select>
                </td>
                <td className="p-1">
                  <input 
                    type="text" 
                    value={row.TRATATIVA} 
                    onChange={e => handleCellChange(row.id, 'TRATATIVA', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-1">
                  <input 
                    type="text" 
                    value={row.STATUS} 
                    onChange={e => handleCellChange(row.id, 'STATUS', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-1">
                  <input 
                    type="text" 
                    value={row["APLICADO POR"]} 
                    onChange={e => handleCellChange(row.id, 'APLICADO POR', e.target.value)}
                    className="w-full p-2 bg-transparent focus:bg-white border border-transparent focus:border-blue-300 rounded outline-none"
                  />
                </td>
                <td className="p-3 text-center">
                  <button 
                    onClick={() => removeRow(row.id)}
                    className="p-1.5 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {data.length === 0 && (
        <div className="p-20 text-center flex flex-col items-center justify-center text-gray-400">
          <AlertCircle className="w-12 h-12 mb-4 opacity-20" />
          <p className="text-sm">Nenhum dado carregado. Faça o upload de um arquivo Excel.</p>
        </div>
      )}
    </div>
  );
};

