import React, { useMemo, useState } from 'react';
import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, 
  LineChart, Line, PieChart, Pie, Cell, ScatterChart, Scatter, ZAxis
} from 'recharts';
import { DeviationRecord, MONTH_MAP } from '../types';
import { Filter, PieChart as PieIcon, BarChart3, TrendingUp, Users, AlertCircle } from 'lucide-react';

interface DashboardProps {
  data: DeviationRecord[];
}

const COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899', '#06b6d4'];

const Dashboard: React.FC<DashboardProps> = ({ data }) => {
  const [filters, setFilters] = useState({
    motorista: 'TODOS',
    status: 'TODOS',
    mes: 'TODOS',
    tratativa: 'TODOS'
  });

  const filteredData = useMemo(() => {
    return data.filter(r => {
      const matchMotorista = filters.motorista === 'TODOS' || r.MOTORISTAS === filters.motorista;
      const matchStatus = filters.status === 'TODOS' || r.STATUS === filters.status;
      const matchMes = filters.mes === 'TODOS' || r["MÊS"] === filters.mes;
      const matchTratativa = filters.tratativa === 'TODOS' || r.TRATATIVA === filters.tratativa;
      return matchMotorista && matchStatus && matchMes && matchTratativa && r.isValid;
    });
  }, [data, filters]);

  // Options for filters
  const options = useMemo(() => {
    const validData = data.filter(r => r.isValid);
    return {
      motoristas: ['TODOS', ...Array.from(new Set(validData.map(r => r.MOTORISTAS)))].sort(),
      status: ['TODOS', ...Array.from(new Set(validData.map(r => r.STATUS)))].sort(),
      // Fix: Explicitly type 'a' and 'b' as string to avoid unknown index type error when accessing MONTH_MAP
      meses: ['TODOS', ...Array.from(new Set(validData.map(r => r["MÊS"])))].sort((a: string, b: string) => (MONTH_MAP[a] || 0) - (MONTH_MAP[b] || 0)),
      tratativas: ['TODOS', ...Array.from(new Set(validData.map(r => r.TRATATIVA)))].sort()
    };
  }, [data]);

  // KPIs
  const kpis = useMemo(() => {
    const totalRegistros = filteredData.length;
    const totalDesvios = filteredData.reduce((acc, curr) => acc + (Number(curr.QTD) || 0), 0);
    const totalTratados = filteredData.filter(r => r.TRATADO === 'SIM').length;
    const percTratado = totalRegistros > 0 ? (totalTratados / totalRegistros * 100).toFixed(1) : 0;

    const motoristaCounts: Record<string, number> = {};
    const desvioCounts: Record<string, number> = {};

    filteredData.forEach(r => {
      motoristaCounts[r.MOTORISTAS] = (motoristaCounts[r.MOTORISTAS] || 0) + Number(r.QTD);
      desvioCounts[r["TIPO DE DESVIO"]] = (desvioCounts[r["TIPO DE DESVIO"]] || 0) + Number(r.QTD);
    });

    const topMotorista = Object.entries(motoristaCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || '-';
    const topDesvio = Object.entries(desvioCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || '-';

    return { totalRegistros, totalDesvios, percTratado, topMotorista, topDesvio };
  }, [filteredData]);

  // Chart Data
  const topMotoristasData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r.MOTORISTAS] = (counts[r.MOTORISTAS] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 10);
  }, [filteredData]);

  const topDesviosData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r["TIPO DE DESVIO"]] = (counts[r["TIPO DE DESVIO"]] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 10);
  }, [filteredData]);

  const timelineData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      const date = r.DATA;
      counts[date] = (counts[date] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([date, value]) => ({ date, value }))
      .sort((a, b) => a.date.localeCompare(b.date));
  }, [filteredData]);

  const statusData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r.STATUS] = (counts[r.STATUS] || 0) + 1;
    });
    return Object.entries(counts).map(([name, value]) => ({ name, value }));
  }, [filteredData]);

  return (
    <div className="space-y-6 pb-20">
      {/* Filters */}
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Motorista</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.motorista}
            onChange={e => setFilters(prev => ({...prev, motorista: e.target.value}))}
          >
            {options.motoristas.map(m => <option key={m} value={m}>{m}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Mês</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.mes}
            onChange={e => setFilters(prev => ({...prev, mes: e.target.value}))}
          >
            {options.meses.map(m => <option key={m} value={m}>{m}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Status</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.status}
            onChange={e => setFilters(prev => ({...prev, status: e.target.value}))}
          >
            {options.status.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-500 uppercase mb-1 block">Tratativa</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-blue-500 outline-none"
            value={filters.tratativa}
            onChange={e => setFilters(prev => ({...prev, tratativa: e.target.value}))}
          >
            {options.tratativas.map(t => <option key={t} value={t}>{t}</option>)}
          </select>
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
        {[
          { label: 'Registros', value: kpis.totalRegistros, icon: <AlertCircle className="w-5 h-5 text-blue-600" />, color: 'bg-blue-50' },
          { label: 'Total Desvios', value: kpis.totalDesvios, icon: <BarChart3 className="w-5 h-5 text-purple-600" />, color: 'bg-purple-50' },
          { label: '% Tratado', value: `${kpis.percTratado}%`, icon: <TrendingUp className="w-5 h-5 text-green-600" />, color: 'bg-green-50' },
          { label: 'Top Motorista', value: kpis.topMotorista, icon: <Users className="w-5 h-5 text-orange-600" />, color: 'bg-orange-50', small: true },
          { label: 'Top Desvio', value: kpis.topDesvio, icon: <PieIcon className="w-5 h-5 text-red-600" />, color: 'bg-red-50', small: true },
        ].map((kpi, i) => (
          <div key={i} className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 flex flex-col justify-between">
            <div className="flex justify-between items-start mb-2">
              <span className="text-xs font-bold text-gray-400 uppercase">{kpi.label}</span>
              <div className={`p-1.5 rounded-lg ${kpi.color}`}>{kpi.icon}</div>
            </div>
            <div className={`font-bold text-gray-800 ${kpi.small ? 'text-sm' : 'text-2xl'}`}>{kpi.value}</div>
          </div>
        ))}
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Bar: Top 10 Drivers */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <Users className="w-4 h-4" /> Top 10 Motoristas (QTD)
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <BarChart data={topMotoristasData} layout="vertical" margin={{ left: 20 }}>
              <CartesianGrid strokeDasharray="3 3" horizontal={false} stroke="#f0f0f0" />
              <XAxis type="number" hide />
              <YAxis dataKey="name" type="category" width={100} fontSize={10} tick={{ fill: '#6b7280' }} />
              <Tooltip cursor={{ fill: '#f9fafb' }} contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Bar dataKey="value" fill="#3b82f6" radius={[0, 4, 4, 0]} barSize={20} />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Line: Trend */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <TrendingUp className="w-4 h-4" /> Tendência de Desvios (QTD por Data)
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <LineChart data={timelineData}>
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f0f0f0" />
              <XAxis dataKey="date" fontSize={10} tick={{ fill: '#6b7280' }} />
              <YAxis fontSize={10} tick={{ fill: '#6b7280' }} />
              <Tooltip contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Line type="monotone" dataKey="value" stroke="#8b5cf6" strokeWidth={3} dot={{ r: 4, fill: '#8b5cf6' }} activeDot={{ r: 6 }} />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Bar: Top 10 Deviations */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <PieIcon className="w-4 h-4" /> Tipos de Desvio (QTD)
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <BarChart data={topDesviosData}>
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f0f0f0" />
              <XAxis dataKey="name" hide />
              <YAxis fontSize={10} tick={{ fill: '#6b7280' }} />
              <Tooltip cursor={{ fill: '#f9fafb' }} contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Bar dataKey="value" fill="#f59e0b" radius={[4, 4, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Pie: Status Distribution */}
        <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100 h-[400px]">
          <h3 className="text-sm font-bold text-gray-700 mb-6 flex items-center gap-2">
            <Filter className="w-4 h-4" /> Distribuição por Status
          </h3>
          <ResponsiveContainer width="100%" height="90%">
            <PieChart>
              <Pie
                data={statusData}
                innerRadius={60}
                outerRadius={100}
                paddingAngle={5}
                dataKey="value"
              >
                {statusData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }} />
              <Legend verticalAlign="bottom" height={36} iconType="circle" />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

