import * as XLSX from 'xlsx';
import { DeviationRecord, MonthName, MONTH_MAP, ValidationError } from '../types';

export const cleanData = (data: any[]): { cleaned: DeviationRecord[], errors: ValidationError[] } => {
  const cleaned: DeviationRecord[] = [];
  const errors: ValidationError[] = [];

  data.forEach((row, index) => {
    const rowIdx = index + 1;

    // Normalize column mapping if the input has slight variations
    const motorista = String(row['MOTORISTAS'] || '').trim();
    const tipoDesvio = String(row['TIPO DE DESVIO'] || '').trim();
    const qtd = parseInt(row['QTD']) || 0;
    let dataRaw = row['DATA'];

    // Basic Validation
    if (!motorista) errors.push({ row: rowIdx, field: 'MOTORISTAS', message: 'Campo obrigatório' });
    if (!tipoDesvio) errors.push({ row: rowIdx, field: 'TIPO DE DESVIO', message: 'Campo obrigatório' });
    if (!dataRaw) errors.push({ row: rowIdx, field: 'DATA', message: 'Campo obrigatório' });

    // Date Processing
    let dateObj: Date | null = null;
    if (typeof dataRaw === 'number') {
      // Excel date format
      dateObj = XLSX.SSF.parse_date_code(dataRaw) ? new Date((dataRaw - 25569) * 86400 * 1000) : null;
    } else if (dataRaw) {
      dateObj = new Date(dataRaw);
    }

    const isValid = motorista && tipoDesvio && dateObj && !isNaN(dateObj.getTime());

    if (dateObj && !isNaN(dateObj.getTime())) {
      const ano = dateObj.getFullYear();
      const mesNum = dateObj.getMonth() + 1;

      // Calculate ISO week
      const d = new Date(dateObj);
      d.setHours(0, 0, 0, 0);
      d.setDate(d.getDate() + 4 - (d.getDay() || 7));
      const yearStart = new Date(d.getFullYear(), 0, 1);
      const semana = Math.ceil((((d.getTime() - yearStart.getTime()) / 86400000) + 1) / 7);

      const mesNome = Object.keys(MONTH_MAP)[mesNum - 1];

      cleaned.push({
        id: crypto.randomUUID(),
        MOTORISTAS: motorista,
        "TIPO DE DESVIO": tipoDesvio,
        QTD: isNaN(qtd) ? 0 : qtd,
        "MÊS": String(row['MÊS'] || mesNome).toUpperCase().trim(),
        TRATADO: String(row['TRATADO'] || 'NÃO').toUpperCase().trim(),
        TRATATIVA: String(row['TRATATIVA'] || '').trim(),
        DATA: dateObj.toISOString().split('T')[0],
        STATUS: String(row['STATUS'] || '').trim(),
        "APLICADO POR": String(row['APLICADO POR'] || '').trim(),
        ANO: ano,
        MES_NUM: mesNum,
        SEMANA: semana,
        isValid: !!isValid
      });
    } else {
      // Keep invalid rows for editing but mark them
       cleaned.push({
        id: crypto.randomUUID(),
        MOTORISTAS: motorista,
        "TIPO DE DESVIO": tipoDesvio,
        QTD: isNaN(qtd) ? 0 : qtd,
        "MÊS": String(row['MÊS'] || '').toUpperCase(),
        TRATADO: String(row['TRATADO'] || 'NÃO').toUpperCase(),
        TRATATIVA: String(row['TRATATIVA'] || ''),
        DATA: String(dataRaw || ''),
        STATUS: String(row['STATUS'] || ''),
        "APLICADO POR": String(row['APLICADO POR'] || ''),
        isValid: false
      });
    }
  });

  return { cleaned, errors };
};

export const exportToExcel = (originalData: DeviationRecord[], treatedData: DeviationRecord[]) => {
  const wb = XLSX.utils.book_new();

  // Função auxiliar para reordenar colunas exatamente como na imagem
  const reorderColumns = (rec: DeviationRecord) => ({
    "MOTORISTAS": rec.MOTORISTAS,
    "TIPO DE DESVIO": rec["TIPO DE DESVIO"],
    "QTD": rec.QTD,
    "MÊS": rec["MÊS"],
    "TRATADO": rec.TRATADO,
    "TRATATIVA": rec.TRATATIVA,
    "DATA": rec.DATA,
    "STATUS": rec.STATUS,
    "APLICADO POR": rec["APLICADO POR"],
    "ANO": rec.ANO,
    "MES_NUM": rec.MES_NUM,
    "SEMANA": rec.SEMANA
  });

  // Create sheet 1: BASE_ATUAL
  const ws1 = XLSX.utils.json_to_sheet(originalData.map(reorderColumns));
  XLSX.utils.book_append_sheet(wb, ws1, "BASE_ATUAL");

  // Create sheet 2: BASE_TRATADA (Cleaned and formatted)
  const ws2 = XLSX.utils.json_to_sheet(treatedData.filter(r => r.isValid).map(reorderColumns));
  XLSX.utils.book_append_sheet(wb, ws2, "BASE_TRATADA");

  XLSX.writeFile(wb, "gestao_desvios_atualizada.xlsx");
};

import React, { useMemo, useState } from 'react';
import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, 
  LineChart, Line, PieChart, Pie, Cell, AreaChart, Area
} from 'recharts';
import { DeviationRecord, MONTH_MAP } from '../types';
import { Filter, PieChart as PieIcon, BarChart3, TrendingUp, Users, AlertCircle, Layers } from 'lucide-react';

interface DashboardProps {
  data: DeviationRecord[];
}

const COLORS = ['#0e4b61', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899', '#06b6d4', '#4ade80', '#fb7185', '#60a5fa'];

const Dashboard: React.FC<DashboardProps> = ({ data }) => {
  const [filters, setFilters] = useState({
    motorista: 'TODOS',
    status: 'TODOS',
    mes: 'TODOS',
    tratativa: 'TODOS'
  });

  const filteredData = useMemo(() => {
    return data.filter(r => {
      const matchMotorista = filters.motorista === 'TODOS' || r.MOTORISTAS === filters.motorista;
      const matchStatus = filters.status === 'TODOS' || r.STATUS === filters.status;
      const matchMes = filters.mes === 'TODOS' || r["MÊS"] === filters.mes;
      const matchTratativa = filters.tratativa === 'TODOS' || r.TRATATIVA === filters.tratativa;
      return matchMotorista && matchStatus && matchMes && matchTratativa && r.isValid;
    });
  }, [data, filters]);

  const options = useMemo(() => {
    const validData = data.filter(r => r.isValid);
    return {
      motoristas: ['TODOS', ...Array.from(new Set(validData.map(r => r.MOTORISTAS)))].sort(),
      status: ['TODOS', ...Array.from(new Set(validData.map(r => r.STATUS)))].sort(),
      meses: ['TODOS', ...Array.from(new Set(validData.map(r => r["MÊS"])))].sort((a: string, b: string) => (MONTH_MAP[a] || 0) - (MONTH_MAP[b] || 0)),
      tratativas: ['TODOS', ...Array.from(new Set(validData.map(r => r.TRATATIVA)))].sort()
    };
  }, [data]);

  const kpis = useMemo(() => {
    const totalRegistros = filteredData.length;
    const totalDesvios = filteredData.reduce((acc, curr) => acc + (Number(curr.QTD) || 0), 0);
    const totalTratados = filteredData.filter(r => r.TRATADO === 'SIM').length;
    const percTratado = totalRegistros > 0 ? (totalTratados / totalRegistros * 100).toFixed(1) : 0;

    const motoristaCounts: Record<string, number> = {};
    const desvioCounts: Record<string, number> = {};

    filteredData.forEach(r => {
      motoristaCounts[r.MOTORISTAS] = (motoristaCounts[r.MOTORISTAS] || 0) + Number(r.QTD);
      desvioCounts[r["TIPO DE DESVIO"]] = (desvioCounts[r["TIPO DE DESVIO"]] || 0) + Number(r.QTD);
    });

    const topMotorista = Object.entries(motoristaCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || '-';
    const topDesvio = Object.entries(desvioCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || '-';

    return { totalRegistros, totalDesvios, percTratado, topMotorista, topDesvio };
  }, [filteredData]);

  // Gráfico para CADA TIPO de desvio (Evolução Mensal Empilhada)
  const deviationTypeEvolution = useMemo(() => {
    const types = Array.from(new Set(filteredData.map(r => r["TIPO DE DESVIO"])));
    const monthData: Record<string, any> = {};

    filteredData.forEach(r => {
      const month = r["MÊS"];
      if (!monthData[month]) {
        monthData[month] = { name: month };
        types.forEach(t => monthData[month][t] = 0);
      }
      monthData[month][r["TIPO DE DESVIO"]] += Number(r.QTD);
    });

    return Object.values(monthData).sort((a: any, b: any) => (MONTH_MAP[a.name] || 0) - (MONTH_MAP[b.name] || 0));
  }, [filteredData]);

  const allDeviationTypes = useMemo(() => {
    return Array.from(new Set(filteredData.map(r => r["TIPO DE DESVIO"])));
  }, [filteredData]);

  const topMotoristasData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r.MOTORISTAS] = (counts[r.MOTORISTAS] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 10);
  }, [filteredData]);

  const statusData = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r.STATUS] = (counts[r.STATUS] || 0) + 1;
    });
    return Object.entries(counts).map(([name, value]) => ({ name, value }));
  }, [filteredData]);

  const deviationDistribution = useMemo(() => {
    const counts: Record<string, number> = {};
    filteredData.forEach(r => {
      counts[r["TIPO DE DESVIO"]] = (counts[r["TIPO DE DESVIO"]] || 0) + Number(r.QTD);
    });
    return Object.entries(counts)
      .map(([name, value]) => ({ name, value }))
      .sort((a, b) => b.value - a.value);
  }, [filteredData]);

  return (
    <div className="space-y-6 pb-20">
      {/* Filters */}
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
        <div>
          <label className="text-xs font-bold text-gray-500 uppercase mb-1 block">Motorista</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-[#0e4b61] outline-none"
            value={filters.motorista}
            onChange={e => setFilters(prev => ({...prev, motorista: e.target.value}))}
          >
            {options.motoristas.map(m => <option key={m} value={m}>{m}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-bold text-gray-500 uppercase mb-1 block">Mês</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-[#0e4b61] outline-none"
            value={filters.mes}
            onChange={e => setFilters(prev => ({...prev, mes: e.target.value}))}
          >
            {options.meses.map(m => <option key={m} value={m}>{m}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-bold text-gray-500 uppercase mb-1 block">Status</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-[#0e4b61] outline-none"
            value={filters.status}
            onChange={e => setFilters(prev => ({...prev, status: e.target.value}))}
          >
            {options.status.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>
        <div>
          <label className="text-xs font-bold text-gray-500 uppercase mb-1 block">Tratativa</label>
          <select 
            className="w-full p-2 border border-gray-200 rounded-lg text-sm bg-gray-50 focus:ring-2 focus:ring-[#0e4b61] outline-none"
            value={filters.tratativa}
            onChange={e => setFilters(prev => ({...prev, tratativa: e.target.value}))}
          >
            {options.tratativas.map(t => <option key={t} value={t}>{t}</option>)}
          </select>
        </div>
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
        {[
          { label: 'Registros', value: kpis.totalRegistros, icon: <AlertCircle className="w-5 h-5 text-[#0e4b61]" />, color: 'bg-cyan-50' },
