import { createClient } from '@supabase/supabase-js';
import { DeviationRecord } from '../types';

// O Supabase injeta essas variáveis automaticamente se configuradas no ambiente
const supabaseUrl = (process.env as any).SUPABASE_URL || '';
const supabaseAnonKey = (process.env as any).SUPABASE_ANON_KEY || '';

export const supabase = createClient(supabaseUrl, supabaseAnonKey);

export const fetchDeviations = async (): Promise<DeviationRecord[]> => {
  const { data, error } = await supabase
    .from('deviations')
    .select('*')
    .order('DATA', { ascending: false });

  if (error) {
    console.error('Erro ao buscar desvios:', error);
    throw error;
  }

  // Mapeia os dados do banco para o nosso formato local se necessário
  return (data || []).map(item => ({
    ...item,
    id: item.id || crypto.randomUUID(),
    isValid: true // Dados vindo do banco são assumidos como válidos
  }));
};

export const upsertDeviations = async (records: DeviationRecord[]) => {
  // Remove campos que não devem ir para o banco (como isValid)
  const dataToUpload = records.map(({ isValid, ...rest }) => rest);

  const { data, error } = await supabase
    .from('deviations')
    .upsert(dataToUpload, { onConflict: 'id' });

  if (error) {
    console.error('Erro ao salvar desvios:', error);
    throw error;
  }

  return data;
};

export const deleteDeviation = async (id: string) => {
  const { error } = await supabase
    .from('deviations')
    .delete()
    .eq('id', id);

  if (error) {
    console.error('Erro ao deletar desvio:', error);
    throw error;
  }
};

import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { DeviationRecord } from './types';
import { cleanData, exportToExcel } from './services/excelService';
import { fetchDeviations, upsertDeviations } from './services/supabaseService';
import DataEditor from './components/DataEditor';
import Dashboard from './components/Dashboard';
import { 
  FileUp, 
  Download, 
  RefreshCw, 
  LayoutDashboard, 
  Table as TableIcon,
  Truck,
  CheckCircle2,
  AlertCircle,
  CloudDownload,
  CloudUpload,
  Loader2
} from 'lucide-react';

const App: React.FC = () => {
  const [data, setData] = useState<DeviationRecord[]>([]);
  const [activeTab, setActiveTab] = useState<'editor' | 'dashboard'>('editor');
  const [loading, setLoading] = useState(false);
  const [syncing, setSyncing] = useState(false);

  // Tenta carregar dados iniciais da nuvem ao abrir o app
  useEffect(() => {
    handleSyncFromCloud();
  }, []);

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setLoading(true);
    const reader = new FileReader();
    reader.onload = (event) => {
      const bstr = event.target?.result;
      const workbook = XLSX.read(bstr, { type: 'binary' });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const rawData = XLSX.utils.sheet_to_json(sheet);

      const { cleaned } = cleanData(rawData);
      setData(cleaned);
      setLoading(false);
    };
    reader.readAsBinaryString(file);
  };

  const handleApplyTreatment = () => {
    const { cleaned } = cleanData(data);
    setData(cleaned);
    alert("Dados tratados com sucesso!");
  };

  const handleSyncFromCloud = async () => {
    setSyncing(true);
    try {
      const cloudData = await fetchDeviations();
      if (cloudData.length > 0) {
        setData(cloudData);
      }
    } catch (err) {
      console.warn("Falha ao sincronizar. Verifique as credenciais do Supabase.");
    } finally {
      setSyncing(false);
    }
  };

  const handleSaveToCloud = async () => {
    if (data.length === 0) return;
    setSyncing(true);
    try {
      await upsertDeviations(data);
      alert("Dados salvos na nuvem com sucesso!");
    } catch (err) {
      alert("Erro ao salvar na nuvem. Verifique sua conexão e permissões.");
    } finally {
      setSyncing(false);
    }
  };

  const handleExport = () => {
    if (data.length === 0) return;
    exportToExcel(data, data);
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50 print:bg-white">
      {/* Header - Oculto na Impressão */}
      <header className="bg-white border-b border-gray-200 px-6 py-4 flex flex-col lg:flex-row items-center justify-between gap-4 sticky top-0 z-50 shadow-sm no-print">
        <div className="flex items-center gap-3">
          <div className="bg-blue-600 p-2 rounded-xl shadow-lg shadow-blue-200">
            <Truck className="w-6 h-6 text-white" />
