import { createClient, SupabaseClient } from '@supabase/supabase-js';
import { DeviationRecord } from '../types';

/** 
 * COLE SEUS DADOS AQUI:
 * 1. SUPABASE_URL: Encontre em Settings -> API -> Project URL
 * 2. SUPABASE_ANON_KEY: É a "Chave publicável" (padrão) que aparece na sua imagem.
 */
const supabaseUrl = 'COLE_AQUI_A_SUA_URL_DO_SUPABASE'; 
const supabaseAnonKey = 'COLE_AQUI_A_SUA_CHAVE_PUBLICAVEL';

// Verifica se as chaves foram preenchidas (não são mais os textos de exemplo)
export const isSupabaseConfigured = 
  supabaseUrl !== 'COLE_AQUI_A_SUA_URL_DO_SUPABASE' && 
  supabaseAnonKey !== 'COLE_AQUI_A_SUA_CHAVE_PUBLICAVEL' &&
  !!supabaseUrl && !!supabaseAnonKey;

export const supabase: SupabaseClient | null = isSupabaseConfigured 
  ? createClient(supabaseUrl, supabaseAnonKey) 
  : null;

// --- Autenticação ---

export const signIn = async (email: string, password: string) => {
  if (!supabase) throw new Error("Supabase não configurado. Verifique as chaves no arquivo services/supabaseService.ts.");
  const { data, error } = await supabase.auth.signInWithPassword({
    email,
    password,
  });
  if (error) throw error;
  return data;
};

export const signOut = async () => {
  if (!supabase) return;
  const { error } = await supabase.auth.signOut();
  if (error) throw error;
};

export const onAuthStateChange = (callback: (session: any) => void) => {
  if (!supabase) return { data: { subscription: { unsubscribe: () => {} } } };
