      const chartDataSum = [{
        name: "Total QTD",
        labels: summaryByTypeData.map(d => d.name),
        values: summaryByTypeData.map(d => d.value)
      }];
      slideSum.addChart(pres.ChartType.bar, chartDataSum, {
        x: 0.5, y: 1.2, w: 9, h: 4.5,
        barDir: "bar",
        showValue: true,
        chartColors: ["0e4b61"]
      });

      await pres.writeFile({ fileName: `Relatorio_Desvios_${new Date().toISOString().split('T')[0]}.pptx` });
    } catch (error) {
      console.error("Erro na exportação PPT:", error);
      alert("Erro ao gerar PowerPoint.");
    } finally {
      setIsExporting(false);
    }
  };

  return (
    <div className="space-y-6 pb-20">
      {/* Header de Ações */}
      <div className="flex flex-col sm:flex-row justify-between items-center gap-4 no-print">
        <div className="flex items-center gap-2">
           <div className="p-2 bg-blue-50 rounded-lg">
            <LayoutDashboard className="w-5 h-5 text-blue-600" />
           </div>
           <h2 className="text-lg font-bold text-gray-800">Relatório Executivo</h2>
        </div>
        <div className="flex gap-2">
          <button onClick={handleExportFiltered} className="flex items-center gap-2 bg-emerald-50 text-emerald-700 hover:bg-emerald-100 px-4 py-2 rounded-lg text-xs font-bold transition-all border border-emerald-200">
            <FileSpreadsheet className="w-4 h-4" /> Excel
          </button>
          <button onClick={handleExportPPT} disabled={isExporting} className={`flex items-center gap-2 px-4 py-2 rounded-lg text-xs font-bold transition-all border border-amber-200 shadow-sm ${isExporting ? 'bg-amber-100 text-amber-400' : 'bg-amber-50 text-amber-700 hover:bg-amber-100'}`}>
            {isExporting ? <Loader2 className="w-4 h-4 animate-spin" /> : <Presentation className="w-4 h-4" />}
            {isExporting ? 'Gerando...' : 'PPT Editável'}
          </button>
          <button onClick={handlePrint} className="flex items-center gap-2 bg-gray-800 text-white hover:bg-gray-900 px-4 py-2 rounded-lg text-xs font-bold transition-all shadow-md">
            <Printer className="w-4 h-4" /> PDF
          </button>
        </div>
      </div>

      {/* Filtros */}
      <div className="bg-white p-4 rounded-xl shadow-sm border border-gray-100 grid grid-cols-1 md:grid-cols-4 gap-4 items-end no-print">
